mapper.select:
SELECT a.* FROM menu a WHERE a.menu_id IN ( SELECT menu_id FROM role_menu WHERE role_id IN ( SELECT ta.role_id FROM user_role ta WHERE ta.user_id = #{userId} ) )
AND a.menu_type = #{menuType}
ORDER BY a.arrangement

